              >
                <div class="text-gray-700">
                  <div class="mb-6 flex flex-wrap items-center justify-between">
                    <div class="text-xl text-baits-blue font-semibold">Provider Details</div>
                    <nuxt-link v-if="userProfile.provider.status !== 'na'" to="/dashboard/profile/provider">
                      <font-awesome-icon :icon="['fas', 'edit']" class="float-right cursor-pointer text-baits-blue w-5 h-5" />
                    </nuxt-link>
                  </div>
                </div>

                <div v-if="userProfile.provider.status == 'na'">
                  <div class="flex flex-col mb-6 text-gray-700">
                    <div class="flex items-center">
                      <div
