                      >
                        <input type="checkbox" class="form-checkbox h-5 w-5 text-baits-blue" id="agree_chk" name="agree" v-model="agree" />
                        <svg class="fill-current hidden w-4 h-4 text-green-500 pointer-events-none" viewBox="0 0 20 20">
                          <path d="M0 11l2-2 5 5L18 3l2 2L7 18z" />
                        </svg>
                      </div>
                      <div class="select-none">I would like to apply to be a service provider!</div>
                    </div>
                    <div class="validate-error-msg">
                      <span class="text-xs" v-if="errors.has('agree')">{{ errors.first('agree') }}</span>
                    </div>
                  </div>
                  <div class="flex w-full">
                    <button
                      v-if="agree"
                      type="button"
                      @click="applyProfile()"
