<script>
import { mapGetters, mapMutations, mapActions } from 'vuex';
import { convertFromUnixTime } from '@/services/index';
import Header from '@/components/dashboard/Header';
// import Profile from '@/components/dashboard/GeneralProfile';
// import Provider from '@/components/dashboard/ProviderProfile';
export default {
  layout: 'dashboard',
  components: {
    Header,
    // Profile,
    // Provider,
  },
  data() {
    return {
      tab: 'home',
      tabs: [
        { text: 'Home', value: 'home' },
        // { text: 'Profile', value: 'profile' },
        // { text: 'Provider', value: 'provider' },
      ],
      agree: false,
    };
  },
  async fetch({ app, params, store }) {
    await store.dispatch('profile/getUserDashboard');
    // if (store.state.user.user.hasOwnProperty('roles') && store.state.user.user.roles.includes('provider')) {
    //   await store.dispatch('bite/getBiteProfile');
    // }
  },
  computed: {
    ...mapGetters({
      userProfile: 'profile/isUserDashboard',
      // user: 'user/getUser',
      // tab: 'profile/getActiveTab',
      // biteProfile: 'bite/isBiteProfile',
    }),
    fullName() {
      return this.userProfile.firstName + ' ' + this.userProfile.lastName;
    },
  },
  methods: {
    ...mapMutations({
      setLoader: 'common/setLoader',
    }),
    ...mapActions({
      applyUserProfile: 'profile/applyUserProfile',
    }),
    convertFromUnixTimeHandler(date) {
      return convertFromUnixTime(date, '');
    },
    async applyProfile() {
      this.setLoader(true);
      const data = {
        application: ['provider'],
      };
      const response = await this.applyUserProfile({ data: data });
      this.setLoader(false);
    },
  },
};
</script>

